import React, { useState } from 'react';
import { Container, Segment, Grid, Button, Form, Divider, Header } from 'semantic-ui-react'

/**
 * (^([\w,.-]+\s)+(\s)+\d{5})+\s+^((\s\[.+\])\s)*
 */

function App() {
  const [query, setQuery] = useState("")
  const [status, setStatus] = useState('')
  const handleSearchChange = (e) => {
    setQuery(e.target.value)
  }
  function clickSearch() {
    fetch("http://127.0.0.1:5000/search?query=" + query)
      .then((response) => response.json())
      .then((data) => {
        if (data.code === 0) {
          setStatus("No Record Found")
        } else {
          setStatus('Author: ' + data.Author)
        }
      })
  }
  return (
    <Container style={{ margin: 20 }}>
      <Segment>
        <Grid columns={2} relaxed="very" stackable>
          <Grid.Row>
            <Grid.Column>
              <Form>
                <Form.Input
                  icon='search'
                  iconPosition='left'
                  placeholder='Search'
                  value={query}
                  onChange={handleSearchChange}
                />
              </Form>
            </Grid.Column>
            <Grid.Column verticalAlign='middle'>
              <Button content="Search" primary onClick={() => clickSearch()} />
              <Button content="Notes" primary />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Header size="small">{status}</Header>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column>
              <Form.TextArea
                id='form-note'
                placeholder='Notes'
              />
            </Grid.Column>
            <Grid.Column>
              <Button content="Notes" primary />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Segment>
    </Container>
  );
}

export default App;
